import React from "react";
import image from "../assets/image.png";

const Section = () => {
  const sectionStyle = {
    backgroundImage: `url(${image})`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    // Add any other background-related styles here
    // Ensure proper height and padding for content
    height: "400px",
    padding: "20px",
    color: "white", // Text color for visibility on the image background
  };

  return (
    <div className="home-container">
      <div className="home-banner-container" style={sectionStyle}>
        <div className="home-text-section">
          <h1 className="primary-heading">
            Your Favourite Food Delivered Hot & Fresh
          </h1>
          <p className="primary-text">
            Healthy switcher chefs do all the prep work, like peeding, chopping
            & marinating, so you can cook a fresh food.
          </p>
          <button className="secondary-button">Your Button</button>
        </div>
        <div className="home-image-section">
          {/* Place any additional images or content here */}
        </div>
      </div>
    </div>
  );
};

export default Section;
